import React, { useState } from 'react';
import airportsData from '../data/airports'; // Importing the airports data

const SearchPage = () => {
  const [search, setSearch] = useState({
    from: '',
    to: '',
    date: ''
  });

  // Handler for form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    // Checking if the selected field is 'from' or 'to'
    if (name === 'from' || name === 'to') {
      // Finding the corresponding city data from the airportsData
      const cityData = airportsData.find(city => city.city === value);
      // Check if the cityData exists and has airports
      if (cityData && cityData.airports.length > 0) {
        const airportCode = cityData.airports[0].code; // Taking the first airport code for simplicity
        setSearch(prevSearch => ({
          ...prevSearch,
          [name]: `${value} (${airportCode})`
        }));
      } else {
        // Handle the case where no airport data is found for the selected city
        console.error('No airport data found for selected city');
      }
    } else {
      // For other inputs, just update the value
      setSearch(prevSearch => ({
        ...prevSearch,
        [name]: value
      }));
    }
  };

  // Handler for form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission, possibly making an API call
    console.log('Form submitted with:', search);
  };

  return (
    <div>
      <h1>Flight Booking System</h1>
      <form onSubmit={handleSubmit}>
        {/* Dropdown for selecting the departure city */}
        <label htmlFor="from">From</label>
        <select name="from" onChange={handleChange} value={search.from}>
          {/* Mapping through the airportsData to create select options */}
          {airportsData.map((entry, index) => (
            <option key={index} value={entry.city}>
              {entry.city}
            </option>
          ))}
        </select>

        {/* Dropdown for selecting the destination city */}
        <label htmlFor="to">To</label>
        <select name="to" onChange={handleChange} value={search.to}>
          {/* Mapping through the airportsData to create select options for destination */}
          {airportsData.map((entry, index) => (
            <option key={index} value={entry.city}>
              {entry.city}
            </option>
          ))}
        </select>

        {/* Input for selecting the date */}
        <label htmlFor="date">Date</label>
        <input
            type="date"
            name="date"
            onChange={handleChange}
            value={search.date}
        />

        {/* Button to submit the form */}
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default SearchPage;
